const order = $json.message_data || {};
const rawPhoneNumber = $json.phone_number || '';

// Adiciona 55 ao n√∫mero de telefone, garantindo que n√£o seja undefined
const formattedPhoneNumber = rawPhoneNumber ? `55${rawPhoneNumber}` : '';

// garante que items √© um array, mesmo que venha undefined
const items = Array.isArray(order.items) ? order.items : [];

const itemsList = items.length
  ? items.map(item => {
      const obs = item.observations ? ` (Obs: ${item.observations})` : '';
      return `‚Ä¢ ${item.quantity}x ${item.name} - R$ ${(item.price * item.quantity).toFixed(2)}${obs}`;
    }).join('\n')
  : 'Nenhum item informado.';

// entrega
const deliveryInfo =
  order.delivery_type === 'delivery'
    ? `\nEndere√ßo: ${order.address || 'N√£o informado'}\nTaxa de Entrega: R$ ${order.delivery_fee || '0,00'}`
    : 'Retirada no Local (Sem taxa)';

const message = `*üéâ Pedido Recebido - C&R Sushi üéâ*\n\n*N√∫mero do Pedido:* ${order.order_number || '-'}\n*Cliente:* ${order.customer_name || '-'}\n*Telefone:* ${rawPhoneNumber || '-'}\n\n*Itens do Pedido:*\n${itemsList}\n\n*Detalhes da Entrega:*\n${deliveryInfo}\n\n*Pagamento:* ${(order.payment_method || 'N√£o informado').toUpperCase()}\n*Total:* R$ ${order.total || '0,00'}\n\nSeu pedido est√° sendo processado. Acompanhe o status pelo link do app!`;

return [
  {
    json: {
      phoneNumber: formattedPhoneNumber,
      message,
      orderNumber: order.order_number
    }
  }
];

const data = $json.message_data;
const rawPhoneNumber = $json.phone_number || '';

// Adiciona 55 ao n√∫mero de telefone, garantindo que n√£o seja undefined
const formattedPhoneNumber = rawPhoneNumber ? `55${rawPhoneNumber}` : '';

const status = data.new_status;
const orderNumber = data.order_number;
const customerName = data.customer_name;
const deliveryType = data.delivery_type;

let statusMessage = '';

switch (status) {
  case 'Pedido recebido':
    statusMessage = `Seu pedido *${orderNumber}* foi recebido com sucesso! Estamos aguardando a confirma√ß√£o do pagamento e a aprova√ß√£o do administrador.`;
    break;
  case 'Em prepara√ß√£o':
    statusMessage = `√ìtimas not√≠cias, ${customerName}! Seu pedido *${orderNumber}* est√° *em prepara√ß√£o*! Em breve estar√° pronto para ${deliveryType === 'delivery' ? 'entrega' : 'retirada'}.`;
    break;
  case 'Pronto para entrega':
    statusMessage = `Seu pedido *${orderNumber}* est√° *pronto para entrega*! O entregador est√° a caminho.`;
    break;
  case 'Saiu para entrega':
    statusMessage = `Seu pedido *${orderNumber}* *saiu para entrega*! Fique de olho, logo ele estar√° com voc√™.`;
    break;
  case 'Entregue':
    statusMessage = `Seu pedido *${orderNumber}* foi *entregue* com sucesso! Esperamos que tenha gostado. Volte sempre!`;
    break;
  case 'Aguardando cliente retirar o pedido':
    statusMessage = `Seu pedido *${orderNumber}* est√° *pronto para retirada* no local. Por favor, venha buscar o quanto antes!`;
    break;
  case 'Cliente j√° fez a retirada':
    statusMessage = `Obrigado, ${customerName}! Seu pedido *${orderNumber}* foi *retirado*. Esperamos que tenha gostado!`;
    break;
  default:
    statusMessage = `O status do seu pedido *${orderNumber}* foi atualizado para: *${status}*.`;
}

const message = `*üç£ Atualiza√ß√£o de Status - C&R Sushi üç£*\n\n*Pedido:* ${orderNumber}\n*Status:* ${status}\n\n${statusMessage}\n\nObrigado por escolher C&R Sushi!`;

return [
  {
    json: {
      phoneNumber: formattedPhoneNumber, // N√∫mero do cliente formatado
      message: message,
      orderNumber: orderNumber
    }
  }
];
